import React from 'react'

import PlaySwitch from '../controls/PlaySwitch'
import ToggleSwitch from '../controls/ToggleSwitch'
import Slider from '../controls/Slider'
import Knob from '../controls/Knob'
import ButtonSet from '../controls/ButtonSet'

export default class Tremolo extends React.Component {
  constructor(props) {
    super(props)
  }

  render() {
    const set = ['sine', 'square', 'triangle', 'sawtooth']

    const {
      name,
      effect,
      wet,
      on,
      toggleEffect,
      changeEffectWetValue,
      changeTremoloDepthValue,
      changeTremoloFrequency
    } = this.props

    return (
      <div className="Effect">
        <ToggleSwitch value="Tremolo" current={on} handleClick={toggleEffect} />

        <div className="controlsContainer">
          <div className="controlsRow">
            <h2>Wet</h2>
            <Slider
              name={name}
              property="wet"
              min="0"
              max="1"
              value={wet}
              handleValueChange={changeEffectWetValue}
            />

            <h2>Frequency</h2>
            <Knob
              min="0"
              max="100"
              value={effect.frequency.value}
              handleValueChange={changeTremoloFrequency}
            />

            <h2>Depth</h2>
            <Knob
              min="0"
              max="1"
              value={effect.depth.value}
              handleValueChange={changeTremoloDepthValue}
            />

          </div>
        </div>
      </div>
    )
  }
}
