import React from 'react'

import PlaySwitch from '../controls/PlaySwitch'
import ToggleSwitch from '../controls/ToggleSwitch'
import Slider from '../controls/Slider'
import Knob from '../controls/Knob'
import ButtonSet from '../controls/ButtonSet'

export default class Freeverb extends React.Component {
  constructor(props) {
    super(props)
  }

  render() {
    const set = ['sine', 'square', 'triangle', 'sawtooth']

    const {
      name,
      effect,
      wet,
      on,
      toggleEffect,
      changeEffectWetValue,
      changeFreeverbRoomSizeValue
    } = this.props

    return (
      <div className="Effect">
        <div className="effectOn">
          <ToggleSwitch
            current={on}
            handleClick={toggleEffect}
            value="Freeverb"
          />
          <h1>Freeverb</h1>
        </div>
        <div className="slider">
            <h2>Wet</h2>
            <Slider
              name={name}
              min="0"
              max="1"
              value={effect.wet.value}
              handleValueChange={changeEffectWetValue}
            />
        </div>
        <div className="slider">
            <h2>Room Size</h2>
            <Slider
              name={name}
              min="0"
              max="1"
              value={effect.roomSize.value}
              handleValueChange={changeFreeverbRoomSizeValue}
            />
        </div>
      </div>
    )
  }
}
